    uv: 26693
  },
  {
    name: "Luggage & Travel Gear", 
    uv: 26300,
  }
]
*/


class GraphPage extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
      numProductResults: [],
      avgPriceResults: [],
    }


  }



  componentDidMount() {

    // route for the number of products per category
    getNumProducts().then(res => {
        this.setState({ numProductResults: res.results})
        console.log("numProducts", res.results)
      })

      // average price route
      getAvgPrice().then(res => {
        this.setState({ avgPriceResults: res.results})
        console.log("avgPrice", res.results)
      })

  }


  render() {
    const blueText = {color: '#1e8d9e'}

    return (
      <div>
        <MenuBar />

          <Row>
        <div>
                <canvas style={{ width: '512px', height: '50px' }}></canvas>
                <h2
                  style={{
                    fontSize: '30px',
                    fontWeight: 'bold',
                    textAlign: 'left',
                    padding: '10px',
                    color: blueText
                  }}
                >
                  {' '}
                  Average Price Per Category:{' '}
                </h2>
                <LineChart width={500} height={300} data={this.state.avgPriceResults}>
                    <XAxis dataKey="name"/>
                    <YAxis/>
                    <CartesianGrid stroke="#eee" strokeDasharray="5 5"/>
                    <Line type="monotone" dataKey="uv" stroke="#8884d8" />
                    <Line type="monotone" dataKey="pv" stroke="#82ca9d" />
                    <Tooltip />
                </LineChart>
              </div>
      <div>
        <canvas style={{ width: '512px', height: '50px' }}></canvas>
        <h2
          style={{
            fontSize: '30px',
            fontWeight: 'bold',
            textAlign: 'right',
            padding: '10px',
          }}
        >
          {' '}
          Number of Products Per Category:{' '}
        </h2>
        <LineChart width={500} height={300} data={this.state.numProductResults}>
            <XAxis dataKey="name"/>
            <YAxis/>
            <CartesianGrid stroke="#eee" strokeDasharray="5 5"/>
            <Line type="monotone" dataKey="uv" stroke="#8884d8" />
            <Line type="monotone" dataKey="pv" stroke="#82ca9d" />
            <Tooltip />
        </LineChart>
      </div>
      </Row>
    </div>

    )
  }

}

export default GraphPage
