import React from 'react';
import { Form, FormInput, FormGroup, Button, Card, CardBody, CardTitle, Progress } from "shards-react";
import {
  Table,
  Pagination,
  Select,
  Row,
  Col,
  Divider,
  Slider,
  Rate
} from 'antd'

import MenuBar from '../components/MenuBar';
import { getCategoriesHelpful, getProductsFromKeywords, getRelatedProducts, getAboveRating, getNumProducts } from '../fetcher';
import styled from 'styled-components';
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip } from 'recharts';

const data = [
  {
    name: "Luxury Watches", 
    uv: 686.24,
  },
  {
    name: "Hamilton Watches", 
    uv: 572.09,
  },
  {
    name: "Omega Watches", 
    uv: 364,
  },
  {
    name: "Breitling Watches", 
    uv: 298,
  },
  {
    name: "Arche", 
    uv: 290,
  },
  {
    name: "Nixon Watches", 
    uv: 281.27,
  },
  {
    name: "Collectible Watches", 
    uv: 274.80,
  },
  {
    name: "Diamonds", 
    uv: 223,
  },
  {
    name: "Wolf Designs", 
    uv: 197.09,
  },
  {
    name: "Fine Jewelry", 
    uv: 171.44,
  }
]

const numData = [
  {
    name: "Women", 
    uv: 461127,
  },
  {
    name: "Shoes & Accesories", 
    uv: 267114,
  },
  {
    name: "Men", 
    uv: 181631,
  },
  {
    name: "Novelty, Costumes, & More", 
    uv: 44002,
  },
  {
    name: "Girls", 
    uv: 44002,
  },
  {
    name: "New Arrivals", 
    uv: 36581,
  },
  {
    name: "Baby", 
    uv: 33063,
  },
  {
    name: "Clothing", 
    uv: 31264,
  },
  {
    name: "Gemstones", 
    uv: 26693
  },
  {
    name: "Luggage & Travel Gear", 
    uv: 26300,
  }
]

const FlexContainer = styled.div`
  display: flex;
`;

const SameRowContainer = styled.div`
  height: 300px;
  width: 100%;
`;


class GraphPage extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
      graphResults: [],
    }


    //this.handleKeyWord2QueryChange = this.handleKeyWord2QueryChange.bind(this)
  }




  /*
  // updates and calls the route for QUERY 7 and puts results into the state
  keywordOnChange() {
    console.log("keyword on change function")
    getProductsFromKeywords(1, this.state.keyWord1Query, this.state.keyWord2Query, this.state.priceLowQuery, this.state.priceHighQuery).then(res => {
      this.setState({ keywordResults: res.results})
      console.log("keyword results", res.results)
    })

  }

  // handles the user typing in new category for QUERY 9
  handleRelatedCategoryChange(value) {
    this.setState({relatedCategory: value})
    console.log("related category updated", this.state.relatedCategory)
    console.log("related category updated", value)
  }

  // calls the route for QUERY 9 when user submits and updates state with the result
  relatedProductsOnChange() {

    getRelatedProducts(1, this.state.relatedCategory).then(res => {
      this.setState({ relatedProductResults: res.results})
      console.log("relatedProducts", res.results)
    })

  }

  */

  componentDidMount() {
    /*
    getNumProducts().then(res => {
        this.setState({ graphResults: res.results})
        console.log("above rating", res.results)
      })
    */
  }


  render() {
    const blueText = {color: '#1e8d9e'}

    return (
      <div>
        <MenuBar />
        <div style={{ width: '80vw', margin: '0 auto', marginTop: '2vh' }}>
          <h3 style = {blueText}>Averages Over Categories</h3>
          </div>
          <SameRowContainer>
        <div>
                <canvas style={{ width: '512px', height: '150px' }}></canvas>
                <h2
                  style={{
                    fontSize: '30px',
                    fontWeight: 'bold',
                    textAlign: 'left',
                    padding: '10px',
                  }}
                >
                  {' '}
                  Average Price Per Category:{' '}
                </h2>
                <LineChart width={500} height={300} data={data}>
                    <XAxis dataKey="name"/>
                    <YAxis/>
                    <CartesianGrid stroke="#eee" strokeDasharray="5 5"/>
                    <Line type="monotone" dataKey="uv" stroke="#8884d8" />
                    <Line type="monotone" dataKey="pv" stroke="#82ca9d" />
                    <Tooltip />
                </LineChart>
              </div>
          </SameRowContainer>
        <SameRowContainer>
      <div>
        <canvas style={{ width: '512px', height: '150px' }}></canvas>
        <h2
          style={{
            fontSize: '30px',
            fontWeight: 'bold',
            textAlign: 'left',
            padding: '10px',
          }}
        >
          {' '}
          Average Number of Products Per Category:{' '}
        </h2>
        <LineChart width={500} height={300} data={numData}>
            <XAxis dataKey="name"/>
            <YAxis/>
            <CartesianGrid stroke="#eee" strokeDasharray="5 5"/>
            <Line type="monotone" dataKey="uv" stroke="#8884d8" />
            <Line type="monotone" dataKey="pv" stroke="#82ca9d" />
            <Tooltip />
        </LineChart>
      </div>
      </SameRowContainer>
    </div>

    )
  }

}

export default GraphPage
